import * as React from "react";
import Header from "../components/shared/header";
import { Tab, Tabs, TabList, TabPanel } from "react-tabs";
import boxEmpty from "./../assets/box-empty.svg";
import ilusEmpty from "./../assets/empty-state.svg";
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import { Hint } from 'react-autocomplete-hint';

import {
  Txtfield,
  DropdownMenu,
  DropdownItem,
  TxtArea,
  BtnPrimary,
} from "./../components/shared/styled";
import "react-tabs/style/react-tabs.css";
import "./../sass/pages/_addJob.scss";
import Footer from "../components/shared/footer";
import ButtonComponent from "../components/shared/atom/button";

import { addJob } from "./../util/job.service";
import { getCategory, getPosition, createPosition, getSkill } from '../util/publication.service';
import { Response } from "../interfaces/Response";
import { Category } from "../interfaces/Category";
import { Position } from "../interfaces/Position";
import TagComponent from "../components/shared/atom/tag";

export default function AddJob() {

  const [encounter, setEncounter] = React.useState(true);
  const [categorys, setCategorys] = React.useState<Category[]>([]);
  const [positions, setPositions] = React.useState<Position[]>([]);
  const [skills, setSkills] = React.useState<any[]>([]);

  const [positionSelected, setPositionSelected] = React.useState<any>({
    id: "",
    id_category: "",
    id_user: "",
    namePosition: "",
    description: ""
  });

  const [job, setJob] = React.useState({
    description: "",
    idCompany: "",
    modality: "",
    timeEstimated: "",
    salaryRange: "",
    funtionsPost: "",
    idCategory: "",
    idPosition: "",
    idSkills: "",
  });

  const [position, setPosition] = React.useState({
    "idCategory": "",
    "namePosition": "",
    "description": "",
  });

  const [skillPayload, setSkillPayload] = React.useState({ idCategory: "", idPositon: "" });

  React.useEffect(() => {
    (async () => {
      await getCategoryAll();
    })()
  }, []);

  const getCategoryAll = async () => {
    const response: Response = await getCategory();
    if (response.status) setCategorys(response.data);
  }

  const handleEvent = (e: any) => {
    setJob({
      ...job,
      [e.target.name]: e.target.value,
    });
  };

  const handleSearch = (e: any) => {
    setEncounter(true);
    const value = e.target.value;
    setPosition({...position, namePosition: value});
    if (positions.length === 0 && value.length > 0) setEncounter(false);

    for (let index in positions) {
      const item = positions[index];
      const title = item.namePosition;
      if (value.length != 0 && title.length != 0) {
        if (title.toLowerCase().search(value.toLowerCase()) != -1) {
          setPositionSelected(item);
          setEncounter(true);
          return;
        }
        setEncounter(false);
      }
    }
  }

  const handlePosition = async (event: any) => {
    const value = event.target.value;
    if (value !== '0') {
      setPosition({...position, idCategory: value});
      const response: Response = await getPosition({ idCategory: value });
      setPositions(response.data);
    }
  }

  const addPosition = async () => {
    const response: Response = await createPosition(position);
    if (response.status) {
      const response: Response = await getPosition({ idCategory: position.idCategory });
      setPositions(response.data);
    }
  }

  const getSkills = async () => {
    setSkillPayload({...skillPayload, idPositon: positionSelected.id, idCategory: positionSelected.id_category});
  }

  React.useEffect(() => {
    (async () => {
      if (skillPayload.idCategory !== "" || skillPayload.idPositon !== "") {
        const response: Response = await getSkill(skillPayload);
        const skillsData: any = response.data;
        const skillsArray = [];
        if (skillsData.skillGeneral.length > 0) {
          for (let i = 0; i < skillsData.skillGeneral.length; i++) {
            skillsArray.push(skillsData.skillGeneral[i]);
          }
        }
        if (skillsData.skillPosition.length > 0) {
          for (let i = 0; i < skillsData.skillPosition.length; i++) {
            skillsArray.push(skillsData.skillPosition[i]);
          }
        }
        if (skillsData.skillPositionUser.length > 0) {
          for (let i = 0; i < skillsData.skillPositionUser.length; i++) {
            skillsArray.push(skillsData.skillPositionUser[i]);
          }
        }
        setSkills(skillsArray);
      }
    })();
  }, [skillPayload]);

  return (
    <React.Fragment>
      <Header />
      <section className="addJobPage">
        <Tabs defaultIndex={1}>
          <TabList className={"mb-5"}>
            <aside className="sideBarMenu mb-5">
              <h3 className="mb-2">Mi Cuenta</h3>
              <span>Gestiona tu cuenta</span>
            </aside>
            <div className="list">
              <Tab>Datos generales</Tab>
              <Tab>Mis publicaciones</Tab>
              <Tab>Proyectos</Tab>
              <Tab>FacturaciÃ³n</Tab>
            </div>
          </TabList>

          <TabPanel>
            <section className="sectionTab">
              <aside className="mb-5">
                <article className="headSection">
                  <h2>Datos Generales</h2>
                </article>
              </aside>

              <p>Datos Personales</p>
              <aside className="FormsRow mt-3">
                <Txtfield
                  onChange={handleEvent}
                  name="name"
                  placeholder="Nombres"
                />
                <Txtfield placeholder="Apellidos" />
              </aside>

              <aside className="FormsRow mt-2 mb-5">
                <Txtfield placeholder="Correo electrÃ³nico" />
                <Txtfield placeholder="TelÃ©fono" />
              </aside>

              <p>Datos de Pago</p>
              <aside className="FormsRow mt-3">
                <DropdownMenu>
                  <DropdownItem>Tipo de comprobante</DropdownItem>
                  <DropdownItem>Recibo por Honorarios</DropdownItem>
                  <DropdownItem>Factura</DropdownItem>
                  <DropdownItem>Recibo</DropdownItem>
                </DropdownMenu>
                <Txtfield placeholder="Nro de comprobante" />
              </aside>
              <aside className="FormsRow mt-2 mb-5">
                <DropdownMenu>
                  <DropdownItem>Banco de destino</DropdownItem>
                  <DropdownItem>Banco de CrÃ©dito del PerÃº</DropdownItem>
                  <DropdownItem>BBVA</DropdownItem>
                </DropdownMenu>
                <Txtfield placeholder="Nro de cuenta" />
              </aside>
              <aside>
                <ButtonComponent type="primary" label="Actualizar" />
              </aside>
            </section>
          </TabPanel>
          <TabPanel>
            <section className="sectionTab">
              <aside className="mb-5">
                <article className="headSection">
                  <h2>Nueva publicaciÃ³n</h2>
                </article>
              </aside>
              <aside className="FormsRow">
                <aside className="FormGroup mt-3">
                  <p>Rubro</p>
                  <DropdownMenu onChange={handlePosition}>
                    <DropdownItem value={'0'}>Elegir rubro</DropdownItem>
                    {categorys.map((category: Category) => <DropdownItem key={category.id} value={category.id}>
                      {category.nameCategory}
                    </DropdownItem>)}
                  </DropdownMenu>
                </aside>
                <aside className="FormGroup mt-3">
                  <p>PosiciÃ³n</p>
                  {/* <Txtfield placeholder="PosiciÃ³n" /> */}
                  <Autocomplete
                    freeSolo
                    id="free-solo-2-demo"
                    disableClearable
                    className="border__none"
                    options={positions.map((option) => option.namePosition)}
                    renderInput={(params) => (
                      <TextField
                        className="border__none"
                        {...params}
                        // label="Nombre de la posiciÃ³n"
                        InputProps={{
                          ...params.InputProps,
                          type: 'search',
                          disableUnderline: true
                        }}
                        onChange={handleSearch}
                        sx={{
                          "& .MuiInputLabel-root": {
                            color: 'transparent'
                          },
                          "& .MuiOutlinedInput-root": {
                            "& > fieldset": {
                              borderRadius: 20,
                              borderColor: "transparent"
                            },
                          }
                        }}
                      />
                    )}
                  />
                </aside>
                <aside className="FormGroup">
                  <br />
                  <BtnPrimary onClick={getSkills}>Buscar</BtnPrimary>
                </aside>
                <aside className="FormGroup">
                  <br />
                  {!encounter && <BtnPrimary onClick={addPosition}>Crear PosiciÃ³n</BtnPrimary>}
                </aside>
              </aside>
              <aside className="FormsRow">
                <aside className="FormGroup mt-3">
                  <p>Skills</p>
                  <article className="skillsBox">
                    {/* <img src={boxEmpty} alt="empty" />
                    <p>Ingrese la posiciÃ³n para cargar skills</p> */}
                    {
                      skills.map((e: any) => <TagComponent type="highlight" level="secondary" label={e.name_skill} />)
                    }
{/*                     
                    <TagComponent type="highlight" level="secondary" label="skill" />
                    <TagComponent type="highlight" level="secondary" label="skill" />

                    <TagComponent type="highlight" level="secondary" label="skill" />                    
                    <TagComponent type="highlight" level="secondary" label="skill" />
                    <TagComponent type="highlight" level="secondary" label="skill" />
                    <TagComponent type="highlight" level="gray" label="skill" /> */}

                  </article>
                </aside>
              </aside>
              <aside className="FormsRow">
                <aside className="FormGroup--full mt-3">
                  <p>DescripciÃ³n</p>
                  <TxtArea placeholder="DescripciÃ³n de la publicaciÃ³n" />
                </aside>
              </aside>
              <aside className="FormsRow">
                <aside className="FormGroup--full mt-3">
                  <p>Funciones</p>
                  <TxtArea placeholder="Funciones a desempeÃ±ar" />
                </aside>
              </aside>
              <aside className="mt-5">
                <h4>Precisiones</h4>
                <aside className="FormsRow">
                  <aside className="FormGroup mt-3">
                    <p>DuraciÃ³n</p>
                    <Txtfield placeholder="DuraciÃ³n de proyecto" />
                  </aside>
                  <aside className="FormGroup mt-3">
                    <p>Presupuesto</p>
                    <Txtfield placeholder="Presupuesto " />
                  </aside>
                  <aside className="FormGroup mt-3">
                    <p>Modalidad</p>
                    <DropdownMenu>
                      <DropdownItem>Elegir modalidad</DropdownItem>
                      <DropdownItem>HÃ­brido</DropdownItem>
                      <DropdownItem>Presencial</DropdownItem>
                      <DropdownItem>Remoto</DropdownItem>
                    </DropdownMenu>
                  </aside>
                </aside>
              </aside>
              <aside className="mt-5">
                <ButtonComponent type="primary" label="Agregar" />
              </aside>
            </section>
            {/* <section className="apply">
              <img src={ilusEmpty} alt="empty" />
              <p>No se encontraron publicaciones</p>
            </section> */}
          </TabPanel>
          <TabPanel>
            <section className="sectionTab">
              <aside className="mb-5">
                <article className="headSection">
                  <h2>Proyectos</h2>
                </article>
              </aside>
              <aside className="FormsRow">
                <article className="proyects">
                  <img src={ilusEmpty} alt="empty" />
                  <p>AÃºn no ingresaste a algÃºn proyecto</p>
                </article>
              </aside>
            </section>
          </TabPanel>
          <TabPanel>
            <section className="sectionTab">
              <aside className="mb-5">
                <article className="headSection">
                  <h2>FacturaciÃ³n</h2>
                </article>
              </aside>
              <aside className="FormsRow">
                <article className="proyects">
                  <img src={ilusEmpty} alt="empty" />
                  <p>Tienes un plan free actualmente</p>
                </article>
              </aside>
            </section>
          </TabPanel>
        </Tabs>
      </section>
      <Footer />
    </React.Fragment>
  );
}
